import React,{Component}from 'react';
import axios from 'axios'; 

class App extends Component{
    constructor(props) {
      super(props);
      this.state = {
         data : [{
           name: 'Product',
           depart: [
             {name: 'Product & Data Science',
              Vp: 'Sundeep Jain'
             },
             {
              name: 'Payment(Uber Money)',
              Head: 'Peter Hazlehurst'
             },
             {
              name: 'Product Design',
              Vp: 'Michael Gough'
             },
             {
              name: 'Global Scaled Solutions',
              Director: 'Megha Yethadka'
             },
             {
              name: 'Business and Developer Products',
              SrDirector: 'Eckart Walther'
             }
           ],
           showDetail: false
         },
         {
           name: 'Technology',
           depart: [
            {name: 'Product Engineering',
             Vp: 'Peeyush Nahar'
            },
            {
             name: 'Mobility & Marketplace',
             Head: 'Guarav Garg'
            },
            {
             name: 'Product Platform(business infrastructure)',
             SrDirector: 'Jennifer Anderson'
            },
            {
             name: 'Infrastructure',
             SrDirector: 'Sumanth Sukumar'
            },
            {
             name: 'Eats Engineering',
             SrDirector: 'Haider Sabri'
            },
            {
              name: 'Technical Program Management & Learning',
              SrDirector: 'Sophia Vicent'
             },
             {
              name: 'Information Technology',
              CIO: 'Shobhana Ahluwalla'
             }
          ],
          showDetail: false
         },
         {
           name: 'Uber ATG',
           depart: [
            {name: 'Software Engineering',
             Vp: 'Jon Thomason'
            },
            {
             name: 'System Engineering & Testing',
             Head: 'Adrian Thompson'
            },
            {
             name: 'Hardware & Vehicle',
             Head: 'Stephen Lesh'
            },
            {
             name: 'Supply Chain',
             Head: 'Sameer Kshirsagar'
            },
            {
             name: 'Safety',
             Head: 'Nat Beuse'
            },
            {
              name: 'Research & Development',
              Head: 'Raquel Urtasun'
             },
             {
              name: 'Product & Design',
              Head: 'Eric Hanson'
             },
             {
              name: 'Stragegy',
              Head: 'Austinn Geidt'
             }
          ],
          showDetail: false
         },
         {
           name: 'Platform Growth & Customer Engagement',
           depart: [
            {name: 'Uber Works',
             CEO: 'Andrey Liscovich'
            },
            {
             name: 'Uber Transit',
             GM: 'David Reich'
            },
            {
             name: 'Customer Engagement',
             Head: 'Jon Venuto'
            },
            {
             name: 'Incubator',
             Head: 'Karishma Shah'
            }
          ],
          showDetail: false
         }]
      }
    }

    handleClick = (group) => {
       let tp = this.state.data.filter((g) => g.name === group.name);
       //console.log(tp[0]);
       tp[0].showDetail = !tp[0].showDetail;
      // console.log('tp is: ', tp[0]);
       this.setState({
         ...this.state,
         data : [...this.state.data.filter((g) => g.name !== group.name), tp[0]]
       })
    }    

    render(){
      return(
        <div>
           Uber
           <ul>
             {this.state.data.map((group, index) => {
               return (

                 <li key = {index}>
                      <div onClick = {() => this.handleClick(group)}>{group.name}</div>
                      {group.showDetail && <p>{group.depart.map((dep, index) => {
                        console.log('dep is: ', dep);
                        return(
                          <li key = {index} className = 'dep'><span>{dep.name}</span>  <span></span> <span className = 'member'> {`${`${Object.keys(dep)[1]}`} :${dep[Object.keys(dep)[1]]}`}</span></li>
                        )
                      })}</p>}
                 </li>

               )
             })}
           </ul>
        </div>
      );
    }
}

export default App;
